{
  title: 'UI/Popup',
  component: PopupComponent,
  decorators: [
    (story) => ({
      moduleMetadata: {
        declarations: [
          ButtonComponent,
          TextComponent,
          DividerComponent,
          HeaderComponent,
        ],
      },
      template: `
        <div style="height: 100vh; background: #f3f4f6; padding: 40px">
          ${story().template}
        </div>
      `,
    }),
  ],
}

{
  title: 'Essential/Button',
  component: ButtonComponent,
  argTypes: {
    variant: {
      control: 'radio',
      options: ['default', 'primary'],
    },
    type: {
      control: 'radio',
      options: ['button', 'submit'],
    },
  },
}

{
  title: 'Essential/Divider',
  component: DividerComponent,
}

{
  title: 'Essential/Header',
  component: HeaderComponent,
}

{
  title: 'Essential/Text',
  component: TextComponent,
  argTypes: {
    muted: { control: 'boolean' },
  },
}

{
  title: 'Navigation/Taskbar',
  component: TaskbarComponent,
  decorators: [
    moduleMetadata({
      imports: [CommonModule, TaskbarComponent, TaskbarItemComponent],
      providers: [
        {
          provide: Router,
          useValue: {
            navigate: () => Promise.resolve(true), // stub navigate method
          },
        },
      ],
    }),
  ],
}

